import Header from './Header';
import { useState, useEffect } from 'react';

const sections = [
    { title: 'Главная страница', url: '/' },
    { title: 'Каталог', url: '/catalogue' },
];

const routeTitles = {
    '/': 'Главная страница',
    '/login': 'Вход',
    '/createnewdb': 'Создание новой базы данных',
    '/signup': 'Регистрация',
    '/catalogue': 'Каталог',
};

function DefaultHeader() {
      const [currentTitle, setCurrentTitle] = useState('Неопределенная страница');

      useEffect(() => {
        setCurrentTitle(routeTitles[window.location.pathname] || 'Неопределенная страница');
      }, []);

    return (
        <Header title={currentTitle} sections={sections} />
    )
}
