import * as React from 'react';
import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Header from '../components/Header';
import Banner from '../components/Banner';
import Post from '../components/Post';
import DefaultFooter from '../components/footer/DefaultFooter';
import image1 from '../images/1.jpg';

const sections = [
  { title: 'Каталог', url: '#' },
  { title: 'Описания', url: '#' },
];

const banner = {
  title: 'Добро пожаловать!',
  description:
    "Это домашняя работа по НИСу. Автор: Карапетян Артём, студент 2 курса ФКН ПИ. Сделана с использованием JavaScript, React, Material UI, Go, SQLite.",
  image: image1,
  imageText: 'main image description',
  linkText: 'Continue reading…',
};

const posts = [
  {
    title: 'Featured post',
    date: 'Nov 12',
    description:
      'This is a wider card with supporting text below as a natural lead-in to additional content.',
    image: 'https://source.unsplash.com/random?wallpapers',
    imageLabel: 'Image Text',
  },
  {
    title: 'Post title',
    date: 'Nov 11',
    description:
      'This is a wider card with supporting text below as a natural lead-in to additional content.',
    image: 'https://source.unsplash.com/random?wallpapers',
    imageLabel: 'Image Text',
  },
];

const defaultTheme = createTheme();

export default function Home() {
  const [posts, setPosts] = React.useState([]);

  React.useEffect(() => {
    fetchPosts(); // Fetch posts when the component mounts
  }, []);

  const fetchPosts = async () => {
    try {
      const response = await fetch('/api/posts'); // Replace '/api/posts' with your Go server endpoint
      const data = await response.json();
      setPosts(data.posts);
    } catch (error) {
      console.error('Error fetching posts:', error);
    }
  };
  return (
    <ThemeProvider theme={defaultTheme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Header title="Главная страница" sections={sections} />
        <main>
          <Banner post={banner} />
          <Grid container spacing={4}>
            {posts.map((post) => (
              <Post key={post.title} post={post} />
            ))}
          </Grid>
        </main>
      </Container>
      <DefaultFooter />
    </ThemeProvider>
  );
}
