import { useState, useEffect } from 'react';

const useServerGoods = () => {
    const [goods, setGoods] = useState([]);
    const [page, setPage] = useState(1);

    const fetchGoods = () => {
        // implementation of fetchGoods function to fetch /goods
    };

    const loadMoreGoods = () => {
        const newGoods = fetchGoods();
        setGoods((prevGoods) => [...prevGoods, ...newGoods]);
        setPage((prevPage) => prevPage + 1);
    };

    useEffect(() => {
        const initialGoods = fetchGoods();
        setGoods(initialGoods);
    }, []);

    return { goods, loadMoreGoods };
};

export default useServerGoods;
