import * as React from 'react';
import { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import { useNavigate } from 'react-router-dom';
import Toolbar from '@mui/material/Toolbar';
import Button from '@mui/material/Button';
import HealingIcon from '@mui/icons-material/Healing';
import Typography from '@mui/material/Typography';
import Link from '@mui/material/Link';
import { useAuth } from '../../AuthContext';


function Header(props) {
    const { titles: titles, sections: initialSections } = props;
    const navigate = useNavigate();
    const [sections, setSections] = React.useState(initialSections);
    const [currentTitle, setCurrentTitle] = useState('Неопределенная страница');
    const [accountName, setAccountName] = useState('');
    const { isLoggedIn, logout } = useAuth();

    useEffect(() => {
        setCurrentTitle(titles[window.location.pathname] || 'Неопределенная страница');
    }, []);

    const handleLoginClick = () => {
        if (isLoggedIn) {
            logout();
            setAccountName('');
        } else {
            navigate('/login');
            setCurrentTitle(titles[window.location.pathname] || 'Неопределенная страница');
        }
    };

    return (
        <React.Fragment>
            <Toolbar sx={{ borderBottom: 1, borderColor: 'divider' }}>
                <HealingIcon sx={{ mr: 2 }} />
                <Typography
                    component="h2"
                    variant="h5"
                    color="inherit"
                    align="left"
                    noWrap
                    sx={{ flex: 1}}
                >
                    {currentTitle}
                </Typography>
                <Button variant="outlined" size="small" onClick={handleLoginClick}>
                    {isLoggedIn ? 'Выйти' : 'Войти'}
                </Button>
            </Toolbar>
            <Toolbar
                component="nav"
                variant="dense"
                sx={{ justifyContent: 'space-between', overflowX: 'auto' }}
            >
                {sections.map((section) => (
                    <Link
                        color="inherit"
                        noWrap
                        key={section.title}
                        variant="body2"
                        href={section.url}
                        sx={{ p: 1, flexShrink: 0 }}
                    >
                        {section.title}
                    </Link>
                ))},
                {isLoggedIn && props.privateSections.map((section) => (
                    <Link
                        color="inherit"
                        noWrap
                        key={section.title}
                        variant="body2"
                        href={section.url}
                        sx={{ p: 1, flexShrink: 0 }}
                    >
                        {section.title}
                    </Link>
                ))}
            </Toolbar>
        </React.Fragment>
    );
}

Header.propTypes = {
    sections: PropTypes.arrayOf(
        PropTypes.shape({
            title: PropTypes.string.isRequired,
            url: PropTypes.string.isRequired,
        }),
    ).isRequired,
    privateSections: PropTypes.arrayOf(
      PropTypes.shape({
          title: PropTypes.string.isRequired,
          url: PropTypes.string.isRequired,
      }),
  ).isRequired,
    titles: PropTypes.objectOf(PropTypes.string.isRequired).isRequired,
};

export default Header;
