import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { Tabs, Tab, Container } from '@mui/material';
import Home from './pages/Home';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import Catalogue from './pages/Catalogue';
import CreateNewDB from './pages/CreateNewDB';
import DefaultFooter from './components/footer/DefaultFooter';
import DefaultHeader from './components/header/DefaultHeader';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';

const sections = [
  { title: 'Главная страница', url: '#' },
  { title: 'Каталог', url: '#' },
  { title: 'Описания', url: '#' },
];

const defaultTheme = createTheme();

function App() {
  const routeTitles = {
    '/': 'Главная страница',
    '/login': 'Вход',
    '/createnewdb': 'Создание новой базы данных',
    '/signup': 'Регистрация',
    '/catalogue': 'Каталог',
  };

  const [currentTitle, setCurrentTitle] = useState('Неопределенная страница');

  useEffect(() => {
    setCurrentTitle(routeTitles[window.location.pathname] || 'Неопределенная страница');
  }, []);

  return (
    <BrowserRouter>
      <ThemeProvider theme={defaultTheme}>
        <CssBaseline />
        <Container maxWidth="lg">
          <Header title={currentTitle} sections={sections} />
          <main>
            <div>
              <Routes>
                <Route exact path="/" element={<Home />} />
                <Route path="/login" component={Login} />
                <Route path="/createnewdb" element={<CreateNewDB />} />
                <Route path="/signup" component={SignUp} />
                <Route path="/catalogue" component={Catalogue} />
              </Routes>
            </div>
          </main>
        </Container>
        <DefaultFooter />
      </ThemeProvider>
    </BrowserRouter>
  );
}

export default App;
