import './App.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { Tabs, Tab, Container } from '@mui/material';
import Home from './pages/Home';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import Catalogue from './pages/Catalogue';
import CreateNewDB from './pages/CreateNewDB';
import DefaultFooter from '../components/footer/DefaultFooter';
import Header from '../components/Header';
import { useLocation } from 'react-router-dom';

const sections = [
  { title: 'Главная страница', url: '#' },
  { title: 'Каталог', url: '#' },
  { title: 'Описания', url: '#' },
];

function App() {
  const location = useLocation();

  // Define an object with route paths as keys and corresponding titles as values
  const routeTitles = {
    '/': 'Главная страница',
    '/login': 'Вход',
    '/createnewdb': 'Создать новую базу данных',
    '/signup': 'Регистрация',
    '/catalogue': 'Каталог',
  };

  const currentTitle = routeTitles[location.pathname] || 'Заголовок по умолчанию';

  return (
    <ThemeProvider theme={defaultTheme}>
      <CssBaseline />
      <Container maxWidth="lg">
        <Header title={currentTitle} sections={sections} />
        <main>
          <BrowserRouter>
            <div>
              <Routes>
                <Route exact path="/" element={<Home />} />
                <Route path="/login" component={Login} />
                <Route path="/createnewdb" element={<CreateNewDB />} />
                <Route path="/signup" component={SignUp} />
                <Route path="/catalogue" component={Catalogue} />
              </Routes>
            </div>
          </BrowserRouter>
        </main>
      </Container>
      <DefaultFooter />
    </ThemeProvider>
  );
}

export default App;
